mapper.select:
SELECT	mbr_id, mbr_pw
FROM members
WHERE mbr_pw = #{mbrPw}
AND mbr_id = #{mbrId}

SELECT *
FROM members  
WHERE mbr_id = #{mbrId}	
AND mbr_pw = #{mbrPw}

SELECT mbr_id
FROM members
WHERE mbr_email = #{mbrEmail}

SELECT mbr_id ,mbr_pw_hint, mbr_pw_answer
FROM members
WHERE mbr_id = #{mbrId}

SELECT mbr_id ,mbr_pw_hint, mbr_pw_answer,mbr_pw 
FROM members
WHERE mbr_pw_hint = #{mbrPwHint}
AND mbr_pw_answer = #{mbrPwAnswer}
AND mbr_id = #{mbrId}

mapper.update:
UPDATE members 
SET mbr_pw = #{mbrPw}
WHERE mbr_pw_hint = #{mbrPwHint}
AND mbr_pw_answer = #{mbrPwAnswer}
AND mbr_id = #{mbrId}

UPDATE members 
SET mbr_pw = #{mbrPw}
WHERE mbr_nm= #{mbrNm}
AND mbr_id = #{mbrId}
